import React, { Component } from "react";
import "./css/Cell.css";
import Cell from "./Cell"

class Game extends Component{
    constructor(props){
        super(props);
        this.state={
            score:0,
            name:props.name,
            data:[]
        }
    }

    addElem(){
        let rr1=Math.floor(Math.random() * 4); 
        let rc1=Math.floor(Math.random() * 4);
        this.state.data[rr1][rc1].full=true;  
        let rr2=Math.floor(Math.random() * 4); 
        let rc2=Math.floor(Math.random()*4);
        while (rr1==rr2 && rc1==rc2){
            rr2=Math.floor(Math.random() * 4); 
            rc2=Math.floor(Math.random()*4);
        }
        this.state.data[rr2][rc2].full=true; 
    }

    createData() {
        let res=[];
        let id=1;
        for (let i=0;i<4;i++){
            for (let j=0;j<4;j++){
            res.push({id:id , kind: "cell" ,full: false,});
            id++;
        }
        this.state.data.push(res);
        res=[];
        }
        
    }

    componentDidMount() {
        this.createData();
        fetch('http://localhost:3001/api/getScore')
            .then(result => result.json())
            .then(res => this.setState({score:res.score}))
    }


    render(){
        const score=this.state.score;
        const data= this.state.data;
        return (<div >
        <div>
        <h1> {score}</h1>
        <p1> Welcome to the game </p1>   
        </div>
        
        <table>
        <tbody>
          {data.length <= 0
            ? "NO DB ENTRIES YET"
            : data.map(dat => (
                <tr  key={dat}  >
                    {dat.map(elem => (
                        <th> 
                        <Cell parm={elem} /> 
                        </th>
                    ))}
                </tr>
              ))}
              </tbody>
                </table>
        
    
        </div>);

    }
} 
export default Game;
