import React, { Component } from "react";
import axios from "axios";
import Field from "./Field";

class Register extends Component {
  constructor(props) {
    super(props);
    this.handleNameChange = this.handleNameChange.bind(this);
    this.handlePasswordChange = this.handlePasswordChange.bind(this);
    this.state = {
      id: 0,
      data: [],
      name: null,
      password: null,
      intervalIsSet: false,
      idToDelete: null,
      idToUpdate: null,
      objectToUpdate: null
    };
  }
  /* componentDidMount() {
    this.getDataFromDb();
    if (!this.state.intervalIsSet) {
      let interval = setInterval(this.getDataFromDb, 1000);
      this.setState({ intervalIsSet: interval });
    }
  }

  componentWillUnmount() {
    if (this.state.intervalIsSet) {
      clearInterval(this.state.intervalIsSet);
      this.setState({ intervalIsSet: null });
    }
  }*/
  handlePasswordChange = value => {
    this.setState({ password: value });
  };

  handleNameChange = name_value => {
    this.setState({ name: name_value });
  };

  deleteFromDB = idTodelete => {
    let objIdToDelete = null;
    this.state.data.forEach(dat => {
      if (dat.id === idTodelete) {
        objIdToDelete = dat._id;
      }
    });

    axios.delete("http://localhost:3001/api/deleteData", {
      data: {
        id: objIdToDelete
      }
    });
  };

  updateDB = (idToUpdate, updateToApply) => {
    let objIdToUpdate = null;
    this.state.data.forEach(dat => {
      if (dat.id === idToUpdate) {
        objIdToUpdate = dat._id;
      }
    });

    axios.post("http://localhost:3001/api/updateData", {
      id: objIdToUpdate,
      update: { password: updateToApply }
    });
  };

  putDataToDB = () => {
    let currentIds = this.state.data.map(data => data.id);
    let idToBeAdded = 0;
    while (currentIds.includes(idToBeAdded)) {
      ++idToBeAdded;
    }
    axios.post("http://localhost:3001/api/putData", {
      id: idToBeAdded,
      name: this.state.name,
      password: this.state.password
    });
  };
  getDataFromDb = () => {
    fetch("http://localhost:3001/api/getData")
      .then(data => data.json())
      .then(res => this.setState({ data: res.data }));
  };
  render() {
    const { data } = this.state;
    return (
      <div>
        
        <Field
          type_field="text"
          content={this.handleNameChange}
          remark="insert name"
        ></Field>

        <Field
          remark="insert password"
          type_field="password"
          content={this.handlePasswordChange}
        />
        <button onClick={() => this.putDataToDB()}>ADD</button>
      </div>
    );
  }
}

export default Register;


