import React, { Component } from "react";
import "./css/App.css";

class Game extends Component{
    constructor(props){
        super(props);
        this.state={
            score:0,
            name:props.name,
            data:[]
        }
    }

    componentDidMount() {
        fetch('http://localhost:3001/api/getScore')
            .then(result => result.json())
            .then(score => this.setState({score}))
    }
    render(){
        return (<div>
        <h1> Welcome {this.state.score}</h1>
        </div>);

    }
} 
export default Game;
