import React, { Component } from "react";
import Field from "./Field"
import Clock from "./Clock"
import axios from "axios";


class App extends Component {

  constructor(props) {
    super(props);
    this.handleNameChange = this.handleNameChange.bind(this);
    this.handlePasswordChange = this.handlePasswordChange.bind(this);
    this.state = {
    data: [],
    id: 0,
    FirsName: null,
    password:null,
    intervalIsSet: false,
    idToDelete: null,
    idToUpdate: null,
    objectToUpdate: null
  };}




  putDataToDB = (name, password) => {
    let currentIds = this.state.data.map(data => data.id);
    let idToBeAdded = 0;
    while (currentIds.includes(idToBeAdded)) {
      ++idToBeAdded;
    }

    axios.post("http://localhost:3001/api/putData", { 
      id: idToBeAdded,
      name: name,
      password:password
      });
  };
  handleNameChange(value) {
    this.setState({FirsName: value});
  }
  handlePasswordChange(value) {
    this.setState({password: value});
  }

  render() {
    return (
      <div>
      <h1 style={{height:'10px' }}> hello world</h1>
        <Field remark="Add to db" content="{this.handleNameChange}"></Field>
        <Clock></Clock>
          <Field remark="create password" content="{this.handlePasswordChange}"></Field>
          <button onClick={() => this.putDataToDB(this.state.FirstName,this.state.password)}>
            ADD
          </button>

        </div>

    );
  }
}

export default App;
