import React, { Component } from "react";
import Field from "./Field"
import Clock from "./Clock"
import axios from "axios";


class App extends Component {
  state = {
    data: [],
    id: 0,
    FirsName: null,
    password:null,
    intervalIsSet: false,
    idToDelete: null,
    idToUpdate: null,
    objectToUpdate: null
  };




  putDataToDB = (name, password) => {
    let currentIds = this.state.data.map(data => data.id);
    let idToBeAdded = 0;
    while (currentIds.includes(idToBeAdded)) {
      ++idToBeAdded;
    }

    axios.post("http://localhost:3001/api/putData", { 
      id: idToBeAdded,
      name: name,
      password:password
      });
  };



  render() {
    return (
      <div>
      <h1 style={{height:'10px' }}> hello world</h1>
        <Field remark="Add to db" name="FirsName"></Field>
        <Clock></Clock>
          <Field remark="create password" name="password" ></Field>
          <button onClick={() => this.putDataToDB(this.state.name,this.state.password)}>
            ADD
          </button>

        </div>

    );
  }
}

export default App;
