import React, { Component } from "react";

class Field extends Component{

    constructor(props) {
        super(props);
        this.state = {
            remark: props.remark,
            name:null
         };
      }
render(){return (<div style={{ padding: "10px" }}>
<input
  type="text"
  onChange={e => this.setState({ name : e.target.value })}
  placeholder={this.state.remark}
  style={{ width: "200px" }}
/></div>);}
}
export default Field;
